continents = ['asia', 'africa', 'north america', 'south america', 'antarctica', 'europe', 'australia']
class CovidAPIView(views.APIView):
    serializer_class = CovidCountryDetails
    permission_classes = (permissions.IsAuthenticated,)
    token_param_config1 = openapi.Parameter('country',in_=openapi.IN_QUERY,description='country name',type=openapi.TYPE_STRING)
    token_param_config2 = openapi.Parameter('continent',in_=openapi.IN_QUERY,description='contient name',type=openapi.TYPE_STRING)
    @swagger_auto_schema(manual_parameters=[token_param_config1,token_param_config2])
    def get(self,request):
        country = request.GET.get('country')
        continent = request.GET.get('continent')
        if country is not None and continent is None:
            country = country.strip().lower().capitalize()
            if country in countries[country[0]]:
                result = requests.get('https://covid-api.mmediagroup.fr/v1/cases?country='+str(country))
                return Response(result.json(),status=status.HTTP_200_OK)
            else:
                result = {'Error' : 'Country not found'}
                return Response(result,status=status.HTTP_404_NOT_FOUND)
        elif country is None and continent is not None:
            continent = continent.strip().lower()
            if continent in continents:
                result = requests.get('https://covid-api.mmediagroup.fr/v1/cases?continent='+str(continent))
                return Response(result.json(),status=status.HTTP_200_OK)
            else:
                result = {'Error' : 'Continent not found'}
                return Response(result,status=status.HTTP_404_NOT_FOUND)
        elif country is not None and  continent is not None:
            result = { 'Warning' : 'Please send either continent or country'}
            return Response(result,status=status.HTTP_400_BAD_REQUEST)
        
        result = requests.get('https://covid-api.mmediagroup.fr/v1/cases/')
        return Response(result.json(),status=status.HTTP_200_OK)         
